mapper.select:
select SUM(resource) 
from shop_sku
where goodId = #{goodId,jdbcType=INTEGER}

mapper.update:
update shop_sku
set
resource = resource - #{goodNum,jdbcType=INTEGER},
sales = sales + #{goodNum,jdbcType=INTEGER}
where id = #{skuId,jdbcType=INTEGER} and resource >=
#{goodNum,jdbcType=INTEGER}
